import React, { useState } from 'react';
import { Route, matchPath, useHistory } from "react-router-dom";

import { ReactComponent as User } from '../../assets/svg/user.svg';
import { ReactComponent as ArrowLeft } from '../../assets/svg/arrow-left.svg';

// Contact Page
const Content = () => (
	<>
		<h1>Content</h1>
		<FakeText />
	</>
);

const FakeText = () => (
	<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
)


function About() {

	const [count, setCounter] = useState('moo')
	// const [moreStuff, setMoreStuff] = useState(...)

	const history = useHistory();

	const navigateToHome = () => {
		history.push("/");
	}

	const navigateToAbout = () => {
		history.push({pathname: "/about", state: {currentPath: 'about'}});
	}
	// useEffect(() => {
	// 	console.log('Do something after counter has changed', counter);
	// }, [counter]);


	const setCount = () => {
		setCounter(count + 1);
		// setMoreStuff(...);
	};
console.log(count)
	return (
		<div className="side left" onClick={setCount}>
			<Route path="/about" component={Content} />
			<ArrowLeft className="left-arrow-icon" onClick={navigateToHome} />
			<h1 className="title" onClick={navigateToAbout}>Mark</h1>
			<User className="user-icon" />
		</div>
	)
}

export default About
