import React, {Fragment, useEffect} from 'react'
// import { Route, Link, useLocation, useHistory } from "react-router-dom";
import {useRouter} from '../RouterHook'

import { ReactComponent as Code } from '../../assets/svg/code.svg';
import { ReactComponent as ArrowRight } from '../../assets/svg/arrow-right.svg';

// Contact Page
const Content = () => (
	<>
		<h1>Content</h1>
		<FakeText />
	</>
);

const FakeText = () => (
	<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
)

const Portfolio = props => {

	const router = useRouter();

	const history = useHistory();
	const { location } = useLocation()
  	useEffect(() => {
		ga('send', 'pageview', location.pathname)
	}, [location]);
	console.log(location)

	const navigateToHome = () => {
		history.push("/");
	}

	const moo = {
		pathname: '/portfolio',
		state: { location: 'portfolio' }
	}

	const navigateToPortfolio = () => {
		history.push(moo);
	}

	return (

		<div className="side right">

			<Code className="code-icon" />

			<h1 className="title" onClick={navigateToPortfolio}>Work</h1>

			<ArrowRight className="right-arrow-icon" onClick={navigateToHome} />

			<Route path="/portfolio" component={Content} />

		</div>


	)
}

export default Portfolio
