import React, { useState } from 'react'
import { Route, useLocation, useHistory } from "react-router-dom";

import { ReactComponent as Code } from '../../assets/svg/code.svg';
import { ReactComponent as ArrowRight } from '../../assets/svg/arrow-right.svg';

const Portfolio = props => {

	const [path, setPath] = useState(null);

	const history = useHistory();
	const location = useLocation();

	// // React.useEffect(() => {
	// // 	console.log('Location changed');
	// // 	setPath('Work');
	// // }, [location, setPath]);

	const navigateToHome = () => {
		history.push({pathname: "/", state: {currentPath: 'home'}});
	}

	const navigateToPortfolio = () => {
		// setPath('Work');
		history.push({pathname: "/portfolio", state: {currentPath: 'work'}});

	}

	console.log('location.pathname: ', location.pathname)
	return (

		<div className="side right">

			<Code className="code-icon" />
			<h1 className="title" onClick={navigateToPortfolio}>Work</h1>
			<ArrowRight className="right-arrow-icon" onClick={navigateToHome} />
			<Route path="/portfolio" component={() => <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>} />

		</div>


	)
}

export default Portfolio
